INSERT INTO _dict_description_items(Name, InsertTime) VALUES (N'Zawodnicza rakieta do rekreacyjnej gry w tenisa ziemnego Head. Optymalna moc i czucie sprawią, że osiągniesz nowy poziom rozgrywki. Grafit nadaje rakiecie wysoki poziom wytrzymałości wytrzymałość, zapewnia optymalne wyważenie i skutecznie redukuje drgania. Zastosowane technologie dadzą Ci przewagę nad rywalem, a precyzyjne parametry ułatwią nadawać piłce więcej dynamiki i zabójczej rotacji podczas każdego zagrania. W sprzedaży z pełnym pokrowcem.', GETDATE());
INSERT INTO _dict_description_items(Name, InsertTime) VALUES (N'Rakieta do tenisa ziemnego wykonana z najmocniejszego i jednocześnie najlżejszego materiału na świecie. Graphene w połączeniu z Youtek pozwolił stworzyć rakietę, która zapewnia wszechstronność, doskonałe czucie, manewrowalność, precyzję i moc oraz dynamikę podczas gry na korcie. Pro to najbardziej agresywna rakieta z rodziny Radical. To większa waga, a więc i większa moc oraz żadnej litości dla rywala zza siatki. Jest polecana przez Andy Murraya dla doświadczonych pasjonatów tenisa i zawodowców. W sprzedaży bez naciągu.', GETDATE());